import React, { useState } from 'react';
import { evaluateRule } from '../services/ruleService';

const RuleEvaluator = () => {
    const [evaluationData, setEvaluationData] = useState({});
    const [result, setResult] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await evaluateRule(evaluationData);
            setResult(response.data.result);
        } catch (error) {
            console.error('Error evaluating rule:', error);
        }
    };

    return (
        <div>
            <h1>Evaluate Rule</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Enter evaluation data"
                    value={JSON.stringify(evaluationData)}
                    onChange={(e) => setEvaluationData(JSON.parse(e.target.value))}
                />
                <button type="submit">Evaluate</button>
            </form>
            {result !== null && <p>Result: {result ? 'True' : 'False'}</p>}
        </div>
    );
};

export default RuleEvaluator;
