import React, { useState } from 'react';
import CombineForm from '../components/CombineForm.js';

const Combine = () => {
    const [combinedAST, setCombinedAST] = useState(null);

    // You can define the rules here directly
    const rules = [
        "(age > 25 AND salary < 50000)",
        "(experience >= 2 AND department = 'Engineering')",
        "(age < 30 OR department = 'Sales')"
    ];

    const handleCombinedAST = (ast) => {
        setCombinedAST(ast);
    };

    return (
        <div>
            <h1>Combine Rules</h1>
            <CombineForm rules={rules} onCombined={handleCombinedAST} />
            {combinedAST && (
                <pre>{JSON.stringify(combinedAST, null, 2)}</pre>
            )}
        </div>
    );
};

export default Combine;
