// src/Pages/Home.js
import React, { useState, useEffect } from 'react';
import RuleForm from '../components/RuleForm.js';
import RuleList from '../components/RuleList.js';
import CombineForm from '../components/CombineForm.js';
import RuleEvaluator from '../components/RuleEvaluator.js';
// import { getAllRules } from '../services/RuleServices.js';

const Home = () => {
    const [rules, setRules] = useState([]);

    useEffect(() => {
        fetchRules();
    }, []);

    const fetchRules = async () => {
        try {
            const response = await getAllRules();
            setRules(response.data.rules); // Adjust based on your API response structure
        } catch (error) {
            console.error('Error fetching rules:', error);
        }
    };

    return (
        <div>
            <h1>Rule Engine</h1>
            <RuleForm onRuleCreated={fetchRules} />
            <CombineForm rules={rules} onCombined={fetchRules} />
            <RuleEvaluator />
            <RuleList rules={rules} />
        </div>
    );
};

export default Home;
