import React, { useState } from 'react';

const RuleForm = ({ onRuleCreated, onCombineRules }) => {
    const [ruleString, setRuleString] = useState('');
    const [rules, setRules] = useState([]);

    const handleRuleSubmit = (e) => {
        e.preventDefault();
        onRuleCreated(ruleString);  // Call parent method to create rule
        setRules([...rules, ruleString]);  // Add rule to local state
        setRuleString('');  // Reset rule input field
    };

    const handleCombineSubmit = () => {
        onCombineRules(rules);  // Call parent method to combine rules
    };

    return (
        <div>
            <form onSubmit={handleRuleSubmit}>
                <label>
                    Rule:
                    <input
                        type="text"
                        value={ruleString}
                        onChange={(e) => setRuleString(e.target.value)}
                    />
                </label>
                <button type="submit">Add Rule</button>
            </form>

            <button onClick={handleCombineSubmit}>Combine Rules</button>
        </div>
    );
};

export default RuleForm;
