import React, { useState } from 'react';

const RuleEvaluator = ({ onEvaluate }) => {
    const [data, setData] = useState({
        age: '',
        department: '',
        salary: '',
        experience: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setData({ ...data, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onEvaluate(data);  // Pass data to parent component for evaluation
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Evaluate Rule</h2>

            <label>
                Age:
                <input
                    type="number"
                    name="age"
                    value={data.age}
                    onChange={handleInputChange}
                />
            </label>
            <br />

            <label>
                Department:
                <input
                    type="text"
                    name="department"
                    value={data.department}
                    onChange={handleInputChange}
                />
            </label>
            <br />

            <label>
                Salary:
                <input
                    type="number"
                    name="salary"
                    value={data.salary}
                    onChange={handleInputChange}
                />
            </label>
            <br />

            <label>
                Experience:
                <input
                    type="number"
                    name="experience"
                    value={data.experience}
                    onChange={handleInputChange}
                />
            </label>
            <br />

            <button type="submit">Evaluate</button>
        </form>
    );
};

export default RuleEvaluator;
