// src/components/CombineForm.js
import React, { useState } from 'react';
import { combineRules } from '../services/RuleServices.js';

const CombineForm = ({ rules, onCombined }) => {
    const [selectedRules, setSelectedRules] = useState([]);

    // Handles form submission to combine rules
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (selectedRules.length === 0) {
            console.error('No rules selected for combination.');
            return;
        }

        try {
            // Send the selected rules for combination
            const response = await combineRules(selectedRules);
            onCombined(response.data.combinedAST); // Pass combined AST to parent component
        } catch (error) {
            console.error('Error combining rules:', error);
        }
    };

    // Handles rule selection (add or remove rules from the selection)
    const handleChange = (rule) => {
        setSelectedRules((prev) =>
            prev.includes(rule) ? prev.filter((r) => r !== rule) : [...prev, rule]
        );
    };

    return (
        <form onSubmit={handleSubmit}>
            {rules.map((rule, index) => (
                <div key={index}>
                    <input
                        type="checkbox"
                        checked={selectedRules.includes(rule)}
                        onChange={() => handleChange(rule)}
                    />
                    <label>{rule}</label>
                </div>
            ))}
            <button type="submit">Combine Rules</button>
        </form>
    );
};

export default CombineForm;
