// src/components/RuleEvaluator.js
import React, { useState } from 'react';
import { evaluateRule } from '../services/RuleServices.js';

const RuleEvaluator = () => {
    const [data, setData] = useState({ age: '', department: '', salary: '', experience: '' });
    const [result, setResult] = useState(null);

    const handleChange = (e) => {
        setData({ ...data, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await evaluateRule(data);
            setResult(response.data.result); // Adjust based on your API response structure
        } catch (error) {
            console.error('Error evaluating rule:', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input name="age" value={data.age} onChange={handleChange} placeholder="Age" />
                <input name="department" value={data.department} onChange={handleChange} placeholder="Department" />
                <input name="salary" value={data.salary} onChange={handleChange} placeholder="Salary" />
                <input name="experience" value={data.experience} onChange={handleChange} placeholder="Experience" />
                <button type="submit">Evaluate Rule</button>
            </form>
            {result !== null && <div>Evaluation Result: {result ? 'True' : 'False'}</div>}
        </div>
    );
};

export default RuleEvaluator;
