import React, { useState } from 'react';
import { combineRules } from '../services/RuleServices.js';

const CombineForm = ({ onCombined }) => {
    const [rules, setRules] = useState(['']);  // Initialize with one empty rule
    const [error, setError] = useState(null); // State for handling any error

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validation to ensure at least one rule is not empty
        if (rules.every((rule) => rule.trim() === '')) {
            setError('Please enter at least one valid rule.');  // Display error if all rules are empty
            return;
        }
        setError(null);  // Reset error if validation passes

        try {
            const response = await combineRules(rules);
            if (response.data && response.data.combinedAST) {
                onCombined(response.data.combinedAST); // Pass the combined AST to parent component
            } else {
                setError('Unexpected response format.'); // Handle unexpected response
            }
        } catch (error) {
            console.error('Error combining rules:', error);
            setError('Failed to combine rules. Please try again.');
        }
    };

    const handleChange = (index, value) => {
        const newRules = [...rules];
        newRules[index] = value;
        setRules(newRules);
    };

    const addRule = () => {
        setRules([...rules, '']); // Add a new empty rule input field
    };

    const removeRule = (index) => {
        const newRules = [...rules];
        newRules.splice(index, 1); // Remove the rule input at the given index
        setRules(newRules);
    };

    return (
        <form onSubmit={handleSubmit}>
            {rules.map((rule, index) => (
                <div key={index} style={{ marginBottom: '10px' }}>
                    <input
                        type="text"
                        placeholder={`Rule ${index + 1}`}
                        value={rule}
                        onChange={(e) => handleChange(index, e.target.value)}
                        required
                    />
                    {rules.length > 1 && (
                        <button type="button" onClick={() => removeRule(index)}>
                            Remove Rule
                        </button>
                    )}
                </div>
            ))}
            <button type="button" onClick={addRule}>
                Add Another Rule
            </button>
            <br />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <button type="submit">Combine Rules</button>
        </form>
    );
};

export default CombineForm;
