import React, { useState } from 'react';
import axios from 'axios';

const EvaluateRule = () => {
    const [ruleString, setRuleString] = useState('');
    const [data, setData] = useState({ age: '', department: '', salary: '', experience: '' });

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Construct the payload directly without nesting
        const payload = {
            ruleString: ruleString, // Directly assign the ruleString
            data: {
                age: Number(data.age), // Ensure this is a number
                department: data.department, // This is fine as a string
                salary: Number(data.salary), // Ensure this is a number
                experience: Number(data.experience) // Ensure this is a number
            }
        };

        // Debugging: Log the payload before sending
        console.log("Payload being sent:", payload);

        try {
            const response = await axios.post('http://localhost:5001/api/rules/evaluate', payload);
            console.log('Evaluation Result:', response.data);
            // Handle the result as needed (e.g., show it on the UI)
        } catch (error) {
            console.error('Error evaluating rule:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4 text-center">Evaluate Rule</h2>
            <div className="mb-4">
                <label className="block text-gray-700">Rule String:</label>
                <input 
                    type="text" 
                    value={ruleString} 
                    onChange={(e) => setRuleString(e.target.value)} 
                    required 
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700">Age:</label>
                <input 
                    type="number" 
                    value={data.age} 
                    onChange={(e) => setData({ ...data, age: e.target.value })} 
                    required 
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700">Department:</label>
                <input 
                    type="text" 
                    value={data.department} 
                    onChange={(e) => setData({ ...data, department: e.target.value })} 
                    required 
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700">Salary:</label>
                <input 
                    type="number" 
                    value={data.salary} 
                    onChange={(e) => setData({ ...data, salary: e.target.value })} 
                    required 
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700">Experience:</label>
                <input 
                    type="number" 
                    value={data.experience} 
                    onChange={(e) => setData({ ...data, experience: e.target.value })} 
                    required 
                    className="mt-1 block w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <button 
                type="submit" 
                className="w-full bg-blue-500 text-white rounded-lg py-2 hover:bg-blue-600 transition duration-300"
            >
                Evaluate Rule
            </button>
        </form>
    );
};

export default EvaluateRule;
