import React, { useState } from 'react';
import axios from 'axios';

const EvaluateRule = () => {
    const [ruleString, setRuleString] = useState('');
    const [data, setData] = useState({ age: '', department: '', salary: '', experience: '' });

    const handleSubmit = async (e) => {
        e.preventDefault();

        const payload = {
            ruleString,
            data
        };

        try {
            const response = await axios.post('http://localhost:5001/api/rules/evaluate', payload);
            console.log('Evaluation Result:', response.data);
            // Handle the result as needed (e.g., show it on the UI)
        } catch (error) {
            console.error('Error evaluating rule:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Rule String:</label>
                <input type="text" value={ruleString} onChange={(e) => setRuleString(e.target.value)} required />
            </div>
            <div>
                <label>Age:</label>
                <input type="number" value={data.age} onChange={(e) => setData({ ...data, age: e.target.value })} required />
            </div>
            <div>
                <label>Department:</label>
                <input type="text" value={data.department} onChange={(e) => setData({ ...data, department: e.target.value })} required />
            </div>
            <div>
                <label>Salary:</label>
                <input type="number" value={data.salary} onChange={(e) => setData({ ...data, salary: e.target.value })} required />
            </div>
            <div>
                <label>Experience:</label>
                <input type="number" value={data.experience} onChange={(e) => setData({ ...data, experience: e.target.value })} required />
            </div>
            <button type="submit">Evaluate Rule</button>
        </form>
    );
};

export default EvaluateRule;
