import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;

export const createRule = async (ruleString) => {
    return axios.post(`${API_URL}/create`, { ruleString });
};

export const combineRules = async (rules) => {
    return axios.post(`${API_URL}/combine`, { rules });
};

export const evaluateRule = async (evaluationData) => {
    return axios.post(`${API_URL}/evaluate`, evaluationData);
};

export const getAllRules = async () => {
    return axios.get(`${API_URL}/rules`);
};
