import axios from "axios";

// const API_URL = process.env.REACT_APP_API_URL;

export const createRule = async (ruleString) => {
  return axios.post(`http://localhost:5001/api/rules/create`, { ruleString });
};

export const combineRules = async (rules) => {
  return axios.post(`http://localhost:5001/api/rules/combine`, { rules });
};

export const evaluateRule = async (evaluationData) => {
  return axios.post(`http://localhost:5001/api/rules/evaluate`, evaluationData);
};

