// Frontend: RuleInput.js
import React, { useState } from 'react';
import axios from 'axios';

const RuleInput = () => {
    const [rules, setRules] = useState(['']); // Initialize with one empty rule input
    const [response, setResponse] = useState(null);

    const handleChange = (index, value) => {
        const newRules = [...rules];
        newRules[index] = value; // Update the specific rule
        setRules(newRules);
    };

    const addRule = () => {
        setRules([...rules, '']); // Add a new empty rule input
    };

    const removeRule = (index) => {
        const newRules = rules.filter((_, i) => i !== index); // Remove the rule at the given index
        setRules(newRules);
    };

    const sendRulesToBackend = async () => {
        try {
            const response = await axios.post('http://localhost:8000/combine_rules', {
                rules: rules.filter(rule => rule.trim() !== '') // Filter out empty rules
            });
            setResponse(response.data); // Store the response from the server
        } catch (error) {
            console.error('Error sending rules to backend:', error);
            setResponse({ message: 'Error sending rules to backend' });
        }
    };

    return (
        <div>
            <h1>Input Rules</h1>
            {rules.map((rule, index) => (
                <div key={index} className="flex items-center space-x-2 mb-2">
                    <input
                        type="text"
                        value={rule}
                        onChange={(e) => handleChange(index, e.target.value)}
                        placeholder={`Rule ${index + 1}`}
                        className="border p-2"
                    />
                    <button onClick={() => removeRule(index)} className="bg-red-500 text-white p-2">Remove</button>
                </div>
            ))}
            <button onClick={addRule} className="bg-blue-500 text-white p-2 mb-4">Add Rule</button>
            <button onClick={sendRulesToBackend} className="bg-green-500 text-white p-2">Send Rules to Backend</button>

            {response && (
                <div className="mt-4">
                    <h2>Response from Server:</h2>
                    <pre>{JSON.stringify(response, null, 2)}</pre>
                </div>
            )}
        </div>
    );
};

export default RuleInput;
