import React, { useState } from 'react';
import RuleForm from '../components/RuleForm.js';
import RuleEvaluator from '../components/RuleEvaluator.js';  
import { createRule, combineRules, evaluateRule } from '../services/RuleServices.js';  // Import necessary services

const Home = () => {
    const [combinedRule, setCombinedRule] = useState(null);
    const [evaluationResult, setEvaluationResult] = useState(null);

    const handleRuleCreated = async (ruleString) => {
        try {
            const response = await createRule(ruleString);  // Create the rule using the API
            setCombinedRule(response.data);  // You can set combinedRule as the newly created rule
        } catch (error) {
            console.error('Error creating rule:', error);
        }
    };

    const handleCombineRules = async (rules) => {
        try {
            const response = await combineRules(rules);  // Combine the rules
            setCombinedRule(response.data);  // Set the combined rule
        } catch (error) {
            console.error('Error combining rules:', error);
        }
    };

    const handleEvaluate = async (data) => {
        try {
            const response = await evaluateRule({ rule: combinedRule, data });  // Pass combinedRule and data for evaluation
            setEvaluationResult(response.data.result);  // Set evaluation result
        } catch (error) {
            console.error('Error evaluating rule:', error);
        }
    };

    return (
        <div>
            <h1>Rule Engine</h1>
            <RuleForm onRuleCreated={handleRuleCreated} onCombineRules={handleCombineRules} />

            {/* Render RuleEvaluator to evaluate the combined rule */}
            <RuleEvaluator onEvaluate={handleEvaluate} />

            {/* Display Evaluation Result */}
            {evaluationResult !== null && (
                <div>
                    <h2>Evaluation Result: {evaluationResult ? 'True' : 'False'}</h2>
                </div>
            )}
        </div>
    );
};

export default Home;
