// src/components/RuleForm.js
import React, { useState } from 'react';
import { createRule } from '../services/RuleServices.js';

const RuleForm = ({ onRuleCreated }) => {
    const [ruleString, setRuleString] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createRule(ruleString);
            setRuleString('');
            onRuleCreated(); // Refresh the list of rules after creation
        } catch (error) {
            console.error('Error creating rule:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={ruleString}
                onChange={(e) => setRuleString(e.target.value)}
                placeholder="Enter your rule"
                required
            />
            <button type="submit">Create Rule</button>
        </form>
    );
};

export default RuleForm;
