import React, { useState } from 'react';
import { combineRules } from '../services/ruleService';

const CombineForm = ({ onCombined }) => {
    const [rules, setRules] = useState(['']);

    const handleAddRule = () => {
        setRules([...rules, '']);
    };

    const handleRuleChange = (index, value) => {
        const newRules = [...rules];
        newRules[index] = value;
        setRules(newRules);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await combineRules(rules);
            onCombined(response.data.combinedAST);
        } catch (error) {
            console.error('Error combining rules:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {rules.map((rule, index) => (
                <div key={index}>
                    <input
                        type="text"
                        placeholder="Enter rule string"
                        value={rule}
                        onChange={(e) => handleRuleChange(index, e.target.value)}
                    />
                </div>
            ))}
            <button type="button" onClick={handleAddRule}>Add Rule</button>
            <button type="submit">Combine Rules</button>
        </form>
    );
};

export default CombineForm;
